import { createContext } from 'react';
import MyComponent from './MyComponent';

export const AppContext = createContext();

function ContextApi(){

    const value = 'My context value';
    alert(value)

    return(
        
        <div>  
            <AppContext.Provider value={value}>
                <MyComponent />
            </AppContext.Provider>
         </div>
    )
}

export default ContextApi;
