import { useState } from 'react';
import './Add.css';
//import { createContext } from 'react';

//export const Information = createContext();

function BoxInformation(prop){

//reason we are using useState here is because we can't access
//a local datatype of a function globally. Be it variable, object etc..
//so we use useState here to grab the value of the local datatype
//inside the method of setItem then store it in the item variable
//outside the scope in the useState declaration.
  const [item, setItem] = useState();

  const AddCart = () => {  

        setItem( {head:prop.title,
                  body:prop.details
                 }
        )

    }
     
    //storing each object properties with their value into a variable
    const itemHead = item.head;
    const itemBody = item.body;

     
    return(
        <div>

     {/*      <Information.Provider >

            </Information.Provider>
      */}   


            <div className="box">
                <h5 id="AddHead">Add header</h5>
                <p>{prop.title}</p>
                <p>{prop.details}</p>
                    
             </div>
             <button className="btn" onClick={AddCart}>Add to cart</button>
    
             <hr></hr>

        </div>
    )
}

export default BoxInformation;
