import './Add.css';

export const Boxlist = createContext();

function BoxInformation(){

//reason we are using useState here is because we can't access
//a local datatype of a function globally. Be it variable, object etc..
//so we use useState here to grab the value of the local datatype
//inside the method of setItem then store it in the item variable
//outside the scope in the useState declaration.
 
const [user, setUser] = useState('Jesse Hall');

 
    return(
        <div>
            <Boxlist.Provider value={user}>
              <h1>Hello {user}</h1>
            </Boxlist.Provider>
      
            
           
    
             <hr></hr>

        </div>
    )
}

export default BoxInformation;
